css:
* {
  padding: 0;
  margin: 0;
  box-sizing: border-box;
}

h1,h2,h3,h4,h6{
    padding:0 !important;
    margin:0 !important;
}

.foote{
    position:fixed;
    bottom:0;

}

.in{
    display: flex;
    align-items:center;
    gap:4px;
}

.info_1, 
.info_2{
    display:flex;
    flex-direction:column;
    justify-content:center;
    padding-top: 10px;
    padding-left:10px;
    gap:1px;
}

.main_logo {
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  border-bottom: 1px solid gray;
  margin: 0px 10px 0px 10px;
}
.logo_left img {
  width: 300px;
  height: 100px;
}
.right_logo h4 {
  text-align: center;
}

/* .................. mian_info */
.mian_info {
  display: flex;
  width: 100%;
}
.info_2 {
  margin-left: 100px;
}
.info_id,
.info_age,
.info_doctor {
  display: flex;
}

.info_id_2,
.info_age_2,
.info_doctor_2 {
  display: flex;
}
/* ...... */
.info_id h4 {
  color: #9478a9;
  padding-left: 20px;
  padding-top: 14px;
}
.info_age h4 {
  color: #9478a9;
  padding-left: 20px;
   padding-top: 14px;
}
.info_doctor h4 {
  color: #9478a9;
  padding-left: 20px;
   padding-top: 14px;
}
/* ....... */
.info_id_2 h4 {
  color: #9478a9;
  padding-left: 20px;
  padding-top: 14px;

}
.info_age_2 h4 {
  color: #9478a9;
  padding-left: 20px;
   padding-top: 14px;
}
.info_doctor_2 h4 {
  color: #9478a9;
  padding-left: 20px;
   padding-top: 14px;
}

.info_id h3,
.info_age h3,
.info_doctor h3 {
  /* color: rgb(56, 12, 100); */
  color: #153b50;
}

/* ...........table_section */
.table_section {
  display: flex;
  justify-content: center;
  /*margin: 7px;*/
  margin-top:10px;
}
table {
  border-collapse: collapse;
}
table th,
table td {
  border-bottom: 1px solid gray;
  border-top: 1px solid gray;
  /*padding: 20px 0px 20px 0px;*/
}
table th {
  /*padding: 10px 30px 10px 30px;*/
  text-align: left;
  color: gray;
  font-size: 14px;
}
table th:nth-child(2) {
  width: 350px;
}

table th:nth-child(6) {
  width: 250px;
}
table td {
  /*padding: 10px 30px 10px 30px;*/
  text-align: left;
  /* color: rgb(90, 71, 109); */
  color: #342042;
}

/* ...........footer */
.signature {
  display: flex;
  justify-content: center;
  height: 100px;
  align-items: center;
  color: #342042;
}
.bukanka {
  display: flex;
  justify-content: center;
  color: #3e30c2;
  border-bottom: 2px dotted #1c146b;
  padding: 10px 0px 10px 0px;
}
.days {
  color: red;
}
.address {
  display: flex;
  justify-content: center;
  padding-top: 20px;
  color: #342042;
}
.address h4 {
  font-size: 14px;
}
.address h4 span {
  padding: 0px 5px 0px 5px;
}
