css:
* {
  padding: 0;
  margin: 0;
  box-sizing: border-box;
}

.main_logo {
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  border-bottom: 1px solid gray;
  margin: 0px 10px 0px 10px;
}
.logo_left img {
  width: 300px;
  height: 100px;
}
.right_logo h4 {
  text-align: center;
}

/* .................. mian_info */
.mian_info {
  display: flex;
  width: 100%;
  justify-content: space-around;
  padding-top: 10px;
}
.info_2 {
  margin-left: 100px;
}
.info_id,
.info_age,
.info_doctor {
  display: flex;
}

.info_id_2,
.info_age_2,
.info_doctor_2 {
  display: flex;
}
/* ...... */
