/* ...........table_section */
.table_section {
  /*display: flex;*/
  /*justify-content: center;*/
  width: 100%;
  padding-top: 10px;
  margin: 10px;
}
table {
    width:100%;
  border-collapse: collapse;
}
table th,
table td {
  border-bottom: 1px solid gray;
  border-top: 1px solid gray;
  padding: 20px 0px 20px 0px;
}
table th {
  padding: 10px 30px 10px 30px;
  text-align: left;
  color: gray;
  font-size: 14px;
}
