css:
* {
  padding: 0;
  margin: 0;
  box-sizing: border-box;

}

/* ..........header__ */
.header__ {
  display: flex;
  flex-direction: column;
  align-items: center;
}
.header__ h3 {
  font-size: 12px;
  font-weight: 500;
}
.header__ h4 {
  font-size: 12px;
}
.reciept_number {
  display: flex;
  padding: 0px 20px 0px 20px;
}
.reciept_number h3 {
  font-weight: 800;
}
.queuue {
  margin-left: 70px;
}
.logo img {
  width: 100px;
  height: 100px;
}

 /*.... main_recpts*/
.main_recpts {
  margin-top: 20px;
  display: flex;
}
.main_recpts h4 {
  color: rgb(56, 55, 55);
}
.right_receipts {
  margin-left: 60px;
}
.patient,
.Mobile {
  margin-top: 20px;
}

/* ........table_section */
.table_section {
  display: flex;
  justify-content: center;
  margin-top: 8px;
}
table {
  border-collapse: collapse;
}
table th,
table td {
  border-bottom: 1px solid black;
  border-top: 1px solid black;
   padding: 20px 0px 20px 0px; 
}
table th {
   padding: 10px 30px 10px 30px; 
  padding: 4px;
  text-align: left;
  color: black;
  font-weight: bold;
  font-size: 14px;
}
table td {
  font-size: 13px;
}
table th:nth-child(1) {
   width: 350px; 
}

table td {
  padding: 4px;
   padding: 10px 30px 10px 30px; 
  text-align: left;
   color: rgb(90, 71, 109); 
  color: #342042;
}

/* ..................main_subtotal */
.main_subtotal {
  width: 60%;
  float: right;
  /*font-size:12px;*/
}
.subtotal {
  display: flex;

  padding: 2px;
  color: black;
  width: 300px;
}

.subtotal h4{
    padding-top: 15px;
    text-align:right;


}
.subtotal h3 {
  padding-left: 30px;
   padding-top: 12px;
}
.discount {
  display: flex;
  padding: 5px 20px 5px 0px;
  width: 300px;
  color: rgb(75, 73, 73);
  border-bottom: 2px dotted rgb(33, 33, 33);
}
.discount h3 {
  padding-left: 40px;
}
.total {
  display: flex;
  padding: 5px 20px 5px 0px;
  width: 300px;
  border-bottom: 2px dotted black;
  color: rgb(43, 43, 43);
  font-weight: bold;
}
.total h3 {
  padding-left: 40px;
}
.paid {
  display: flex;
  padding: 5px 20px 5px 0px;
  width: 300px;
  border-bottom: 2px dotted black;
  color: rgb(43, 43, 43);
  font-weight: bold;
}
.paid h3 {
  padding-left: 60px;
}
.balance {
  display: flex;
  padding: 5px 20px 5px 0px;
  border-bottom: 2px dotted black;
  width: 300px;
  color: rgb(43, 43, 43);
  font-weight: bold;
}
.balance h3 {
  padding-left: 45px;
}

h1,
h2,
h3,
h4,
h5,
h6{
    padding: 0;
    margin: 0;
}


/* ........mediacare */
.mediacare{
    /*margin-top: 200px;*/
    display: flex;
    flex-direction: column;
    align-items: center;
}
.mediacare img{
    width: 250px;
    height: 40px;
}
.mediacare h3{
    font-size: 12px;
  color: rgb(77, 76, 76);
}
@media print{

.mediacare{
    margin-top: 200px;
    margin-left: 0px;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding:0px;

}
.main_subtotal {
  width: 40%;
  float: right;
}
}
